package com.microsoft.credentialstorage.implementation.macosx;

import com.microsoft.credentialstorage.model.StoredTokenPair;
import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.assertArrayEquals;
import static org.junit.Assert.assertNull;
import static org.junit.Assume.assumeTrue;

public class KeychainSecurityBackedTokenPairStoreIT {

    private KeychainSecurityBackedTokenPairStore underTest;

    @Before
    public void setup() {
        assumeTrue(KeychainSecurityCliStore.isSupported());

        underTest = new KeychainSecurityBackedTokenPairStore();
    }

    @Test
    public void e2eTestStoreReadDelete() {
        final char[] sampleAccessToken = "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6IlliUkFRUlljRV9tb3RXVkpLSHJ3TEJiZF85cyIsImtpZCI6IlliUkFRUlljRV9tb3RXVkpLSHJ3TEJiZF85cyJ9..olzZDq52saLRuCxyilW6IgUl1TOudryfaJmwvDadQHAdfqe6KgaN7pE9gm3Wg7CTI0ElzQlVA2BdG2oX-DqBZZjNlvy0Wk9LMOMq6NzR-mf4ksqy1NN_i__cC5WffmQwaE3K-tk6A0llE2e4qjV162EER-ZIfwlAi6uwrJv5vq2UyEGYAU1XqBQ439RxcYG5enYQWB83i0ju-Jl-j-ABjtCmz4UKco96O7xKR9G2eTRLp411Lrn1BTEoNWVMCBzTafDY4wCGSrIWo-KarnJC8o95nKFhxyuBuwYyhMJW_oPVoq6XeL2Z8kCtBc_nJKicS21nazmVHe6Cpijev38CeA".toCharArray();
        final char[] sampleRefreshToken = "AABAAAA0TWENU4YUUq5uvDvmnaQie4s-6IJVJQZvwtU53i_arTnRLnbhuIsa2YbtXbPpaP6w8Zr3nNeN3av03Dz2yAyMg9KjFKHEU4nxmM1K2H30sywVia1g78emt20KZEJ5ScaWIowzCWxCRZPpatu5Ktf1q2b1lwtlvvI5gUF5IntfwxMiiR5DFMh51HYeHcISDGi-l1rpoyQ4qWqBPRJ_8GMIlM_YwX2YS0m8iLNlVIhz2BgQa2Ic10SzBlvIZkNXlLN1EBxOzHE5Q93HGaJtOXrAyFzXPUv3FyvsYzbhfF5PHX9YnBrinWO9bhCSIZNAx9PHw_XL9LWhad6MXO1zykYSVlss2VUrz9Cuz4SVonS1bokLWBVlITr1hYbL68qJOWYsgoNc_yWMyTMdeCDcFsZGC0EVClrMsE7KW39CebZzF9g7QH6prsHGT1sqKUh1F54oCQSb7zV4A2WsRfxvoE8jS3_r0BWwZb5frUKt0ZO1A080sGag7mwtTcanVB2U2oqkpVOJhfV3NqCEshvk94AhG3Dm-gl4nrdhcvAlOVgg9UFvbLoVSb44m1CcxxBdTybW1Kzd44wdR63r_dA1CW_11ZzDOV792MPgyUtJqaKXYcgAA".toCharArray();

        final StoredTokenPair tokenPair = new StoredTokenPair(sampleAccessToken, sampleRefreshToken);
        final String key = "KeychainTest:http://test.com:Token";

        // this should have been saved to cred manager, it would be good if you can set a breakpoint
        // and manaully verify this now
        underTest.add(key, tokenPair);

        final StoredTokenPair readToken = underTest.get(key);

        assertArrayEquals("Retrieved Access Token is different", sampleAccessToken, readToken.getAccessToken().getValue());
        assertArrayEquals("Retrieved Refresh Token is different", sampleRefreshToken, readToken.getRefreshToken().getValue());

        // The credential under the specified key should be deleted now
        underTest.delete(key);

        final StoredTokenPair nonExistentToken = underTest.get(key);
        assertNull(nonExistentToken);

    }
}